import groovy.json.JsonOutput

def flowFile = session.get()
if (!flowFile) return

try {
    def chatId = flowFile.getAttribute('telegram.chat_id')
    def orderId = flowFile.getAttribute('order_short_id')
    def loyaltyCard = flowFile.getAttribute('loyalty_card')
    def storeCode = flowFile.getAttribute('store_code') 
    def appointment = flowFile.getAttribute('commentary')
    //def orderStatus = flowFile.getAttribute('order_status')
	def orderStatus = "Заказ оплачен"

    // Функция экранирования
    def escapeMd = { text -> 
        if (!text) return ""
        text.replaceAll(/[_*\[\]()~`>#+\-=|{}.!-]/) { "\\" + it }
    }

    // Собираем сообщение через StringBuilder
    def messageBuilder = new StringBuilder()
    messageBuilder.append("✅ *Заказ\\!*\n\n")
    messageBuilder.append("*Номер заказа:* `").append(escapeMd(orderId)).append("`\n")
    messageBuilder.append("*Клиент:* `").append(escapeMd(loyaltyCard)).append("`\n")
    messageBuilder.append("*Магазин:* `").append(escapeMd(storeCode)).append("`\n")
    messageBuilder.append("*Услуга:* `Кабина красоты`\n")
    messageBuilder.append("*Дата и время:* `").append(escapeMd(appointment)).append("`\n")
    messageBuilder.append("*Статус:* `").append(escapeMd(orderStatus)).append("`")

    def telegramRequest = [
        "chat_id": chatId,
        "text": messageBuilder.toString(),
        "parse_mode": "MarkdownV2"
    ]

    def jsonOutput = JsonOutput.toJson(telegramRequest)

    flowFile = session.write(flowFile, { inputStream, outputStream ->
        outputStream.write(jsonOutput.getBytes('UTF-8'))
    } as org.apache.nifi.processor.io.StreamCallback)

    flowFile = session.putAttribute(flowFile, 'mime.type', 'application/json')
    flowFile = session.putAttribute(flowFile, 'telegram.endpoint', 'sendMessage')

    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception e) {
    log.error("Order format error: ${e.message}")
    session.transfer(flowFile, REL_FAILURE)
}

