import org.apache.nifi.processor.io.StreamCallback
import groovy.json.JsonOutput

def flowFile = session.get()
if (!flowFile) return

try {
    def chatId = flowFile.getAttribute('telegram.chat_id')
    def responseText = flowFile.getAttribute('response.text')

    // Создаем базовый запрос
    def telegramRequest = [
        "chat_id": chatId,
        "text": responseText
    ]

    // Добавляем опциональные поля
    def parseMode = flowFile.getAttribute('response.parse_mode')
    if (parseMode) {
        telegramRequest.parse_mode = parseMode
    }

    def replyMarkupJson = flowFile.getAttribute('response.reply_markup')
    if (replyMarkupJson) {
        telegramRequest.reply_markup = new groovy.json.JsonSlurper().parseText(replyMarkupJson)
    }

    // Записываем JSON в содержимое
    def jsonOutput = JsonOutput.toJson(telegramRequest)

    flowFile = session.write(flowFile, { inputStream, outputStream ->
        outputStream.write(jsonOutput.getBytes('UTF-8'))
    } as StreamCallback)

    // Устанавливаем атрибуты для HTTP
    flowFile = session.putAttribute(flowFile, 'mime.type', 'application/json')
    flowFile = session.putAttribute(flowFile, 'telegram.endpoint', 'sendMessage')

    session.transfer(flowFile, REL_SUCCESS)
} catch (e) {
    log.error("Ошибка постобработки: ${e.message}")

    // Fallback error
    def errorJson = '{"error": "processing_error"}'
    flowFile = session.write(flowFile, { inputStream, outputStream ->
        outputStream.write(errorJson.getBytes('UTF-8'))
    } as StreamCallback)

    session.transfer(flowFile, REL_FAILURE)
}

